'use strict';

/**
* Configure routes for base application
* Do not configure tool specific routes here!
*/
var Routes = ($routeProvider, $compileProvider) => {

	// Allow cordova files for image src
	$compileProvider.imgSrcSanitizationWhitelist(/^\s*(https?|ftp|file|cdvfile|ms-appx|x-wmapp0e):|data:image\//);
	$compileProvider.aHrefSanitizationWhitelist(/^\s*(https?|ftp|mailto|file|ghttps?|ms-appx|x-wmapp0):/);

	$routeProvider
	.when('/boot',						{templateUrl : 'base/partials/boot.html',				controller : 'BootCtrl'})
	.when('/boot/:moduleId',			{templateUrl : 'base/partials/boot.html',				controller : 'BootCtrl'})
	.when('/home',						{templateUrl : 'base/partials/toolSelect.html',			controller : 'HomeCtrl'})
	.when('/settings', 					{templateUrl : 'base/partials/settings.html',			controller : 'SettingsCtrl' })
	.when('/settings-push', 			{templateUrl : 'base/partials/settings-push.html',		controller : 'SettingsPushCtrl' })
	.when('/sync', 						{templateUrl : 'base/partials/sync.html',				controller : 'SyncCtrl' })
	.when('/sync-configure', 			{templateUrl : 'base/partials/sync-configure.html',		controller : 'SyncConfigureCtrl' })
	.when('/sync-progress', 			{templateUrl : 'base/partials/sync-progress.html',		controller : 'SyncProgressCtrl' })
	.when('/register', 					{templateUrl : 'base/partials/login.html',				controller : 'RegisterCtrl' })
	.when('/register-selectModules',	{templateUrl : 'base/partials/selectModules.html',		controller : 'RegisterSelectModuleCtrl' })
	.when('/register-modulesRegistration', {templateUrl : 'base/partials/registerModules.html',	controller : 'RegisterModuleRegistrationCtrl' })
	.otherwise({ redirectTo : '/boot' });
};
Routes.$inject = ['$routeProvider', '$compileProvider'];
export default Routes;

'use strict';
/**
 * Class for utility methods.
 * This class should only be used for simple utility methods.
 * DO NOT use this class to call services or to do tool specific
 * manipulations - these kind of functionality should be written in controllers or services
 * for the tool
 */
export function getNumberOfProperties(object, field){
	if (object === undefined){
		return 0;
	}
	var toTest = object;
	if (field){
		toTest = object[field];
	}
	return Object.keys(toTest).length;
}

/**
 * Convert an unknown jsonData type to an JSON Object
 */
export function convertToString(jsonData){
	if (typeof jsonData === 'string'){
		return jsonData;
	}
	else if (typeof jsonData === 'object'){
		return JSON.stringify(jsonData);
	}
	else{
		return '';
	}
}


/**
 * Convert an unknown jsonData type to an JSON Object
 */
export function convertToObject(jsonData){
	if (typeof jsonData === 'string'){
		return JSON.parse(jsonData);
	}
	else if (typeof jsonData === 'object'){
		return jsonData;
	}
	else{
		return null;
	}
}


/**
 * Returns a unique ID immediately
 */
export function generateUID(){
	return 'UNIPOOLE_xxxxxxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
		var r = Math.random() * 16 | 0, v = c == 'x' ? r : r & 0x3 | 0x8;
		return v.toString(16);
	});
}

'use strict';
var BootCtrl = ($q, $rootScope, $location, $routeParams, DataService, UserService, HomeService, SyncAPIService, SynthErrorHandler, SyncService, PushService, UserSettings, ModuleService) => {
	$rootScope.activePage = 'boot';
	$rootScope.breadcrumbs = null;

	$rootScope.applicationBoot = true;

	var settings;
	// Make sure there is a no media scan file in place
	var funcGetSettings = function(){
			return UserSettings.getSettings().then(function(userSettings){
				settings = userSettings;
			});
		},

		funcCheckNoMediaFile = function(){
			return DataService.ensureNoMediaScanFiles();
		},
		// Check if the user is registered
		funcCheckRegistrationProgress = function(){
			return UserService.getRegistrationProgress();
		},
		funcGoHomePromise = function(){
			return HomeService.getHomeTools()
			.then(function(modules){
				/*
				* If there is only one tool, then we can go directly to that tool if the settings
				* indicate that we may
				*/
				if(modules.length === 1 && modules[0].tools.length === 1 && settings.singleToolMenu === false){
					history.pushState(null, 'Home', '#/home'); // Fake coming from home
					$location.path(`/tool/${modules[0].tools[0].key}/${modules[0].id}`);
				}
				else{
					$location.path('/home');
				}
			});
		},
		funcGetProgressPromise = function(progress){
			if(UserService.PROGRESS_COMPLETED === progress){
				// If the user is registered we will start the background sync
				SyncService.startBackgroundSync();

				// We don't need to wait for this to complete, it must just happen
				PushService.registerDevice();

				// Check if there are new modules for the user
				ModuleService.hasNewModules().then((hasNewModules) => {
					$rootScope.applicationBoot = false;
					if(hasNewModules){
						history.pushState(null, 'Home', '#/home'); // Fake coming from home
						$location.path('/register-selectModules');
						return $q.when(true);
					}
					else {
						// If not, check where "home" is going to be
						return funcGoHomePromise();
					}
				}, () => {
					$rootScope.applicationBoot = false;
					// If we couldn't check online for new modules, we go back home
					return funcGoHomePromise();
				});


			}
			else if(UserService.PROGRESS_SELECT_MODULES === progress){
				$location.path('/register-selectModules');
				$rootScope.applicationBoot = false;
			}
			else{
				$location.path('/register');
				$rootScope.applicationBoot = false;
			}
		};

	funcGetSettings()
	.then(funcCheckNoMediaFile)
	.then(funcCheckRegistrationProgress)
	.then(funcGetProgressPromise, (reason) =>{
		$rootScope.applicationBoot = false;
		return SynthErrorHandler(reason);
	});
};
BootCtrl.$inject = ['$q', '$rootScope', '$location', '$routeParams', 'DataService',
 'UserService', 'HomeService', 'SyncAPIService', 'SynthErrorHandler', 'SyncService',
 'PushService', 'UserSettings', 'ModuleService'];

export default BootCtrl;

'use strict';
class AppController {

	constructor($scope, $routeParams, UserSession, UserService, SynthConfig, SyncService, SynthError){
		// This is not a real error, this only initialises the SynthError class
		SynthError(1000);

		/**
		* Callback function to open the vendor website
		*/
		$scope.linkVendor = function(){
			window.open(SynthConfig.vendorURL, '_system');
		};

		// Name of the vendor for the application
		$scope.vendorName = SynthConfig.vendorName;

		// Name of the application
		$scope.applicationName = SynthConfig.applicationName;

		// User session object
		$scope.userSession = UserSession;

		// The initial sync background task is started by the boot process
		function onPaused(){
			SyncService.stopBackgroundSync();
		}

		function onResume(){
			SyncService.startBackgroundSync();
		}

		document.addEventListener('pause', onPaused, false);
		document.addEventListener('resume', onResume, false);

		// Set default options for some scrollers
		$scope.myScrollOptions = $scope.myScrollOptions || {};
		$scope.myScrollOptions.menuScroll = {
			scrollbars : true
		};
		$scope.myScrollOptions.mainScroll = {
			scrollbars : true,
			scrollX : true
		};

		var showingExitPrompt = false;

		/**
		* Function to go back from the button on the screen
		*/
		$scope.goBack = function(){
			if ($scope.activePage === 'home' || $scope.activePage === 'register' || $scope.activePage === 'boot'){
				// iOS cannot exit an application
				if(!showingExitPrompt && device.platform !== 'iOS'){
					navigator.notification.confirm(
							'Are you sure you want to exit the application?',
							function(index){
								showingExitPrompt = false;
								if (index === 2){
									navigator.app.exitApp();
								}
							},
							'Exit Application',
							['No', 'Yes']);
				}
			}
			else{
				navigator.app.backHistory();
			}
		};

		/**
		* Redirect the native back button to the same handler when the user
		* clicks the back button on the application
		*/
		document.addEventListener('backbutton', function(){
			$scope.goBack();
		}, false);
	}
}
AppController.$inject = ['$scope', '$routeParams', 'UserSession', 'UserService', 'SynthConfig', 'SyncService', 'SynthError'];
export default AppController;

'use strict';

/**
 * Directive creating a texual animation of ellipse ... .. .
 */
class AnimatedEllipse{

	/**
	 * Creates a new instance of this directive
	 */
	constructor($interval){
		/**
		 * A reference to $interval.
		 */
		this.$interval = $interval;

		/**
		 * Directive restricted to Element
		 */
		this.restrict = 'E';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = false;

		const self = this;

		/**
		 * Linking function.
		 */
		this.link = function(){
			self.linkDirective(...arguments);
		};
	}

	/**
	 * Link function for the directive
	 */
	linkDirective(scope, element, attrs){

		var timerId = null,
			running = false,
			state = 0;
		const $interval = this.$interval;

		element.text('\u00a0\u00a0\u00a0');

		// Watch for change in the busy flag
		scope.$watch(attrs.busy, function(value) {
			toggleTimer(value);
		});

		// Make sure to stop timers when the element gets destroyed
		element.on('$destroy', function() {
			toggleTimer(false);
		});


		function toggleTimer(run){
			// If we need to run, and we are not yet running
			if(run && !running){
				running = true;
				state = 0;
				timerId = $interval(function() {
					updateAnimation(); // update DOM
				}, 1100);
			}
			// Else id we need to stop and we are running
			else if(!run && running){
				$interval.cancel(timerId);
				element.text('\u00a0\u00a0\u00a0');
				running = false;
			}
		}


		function updateAnimation(){
			switch(state){
			case 0:
				element.text('.\u00a0\u00a0');
				break;
			case 1:
				element.text('..\u00a0');
				break;
			case 2:
				element.text('...');
				break;
			}

			if(state == 2){
				state = 0;
			}
			else{
				state = state + 1;
			}
		}
	}
}

/**
 * Factory to create an instance of the directive
 */
var AnimatedEllipseFactory = function(){
	return new AnimatedEllipse(...arguments);
};
AnimatedEllipseFactory.$inject = ['$interval'];
export default AnimatedEllipseFactory;

'use strict';

/**
 * Spinner used during application boot
 */
class BootSpinner {

	/**
	 * Creates a new instance of the BootSpinner directive
	 */
	constructor(){

		/**
		 * Directive restricted to Element
		 */
		this.restrict = 'E';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = false;

		/**
		 * Linking function.
		 */
		this.link = function(scope, element){
			var opts = {
				lines : 15, // The number of lines to draw
				length : 0, // The length of each line
				width : 30, // The line thickness
				radius : 90, // The radius of the inner circle
				corners : 1, // Corner roundness (0..1)
				rotate : 0, // The rotation offset
				direction : 1, // 1: clockwise, -1: counterclockwise
				color : '#000', // #rgb or #rrggbb or array of colors
				speed : 1, // Rounds per second
				trail : 35, // Afterglow percentage
				shadow : false, // Whether to render a shadow
				hwaccel : true, // Whether to use hardware acceleration
				className : 'spinner', // The CSS class to assign to the spinner
				zIndex : 2e9, // The z-index (defaults to 2000000000)
				top : '50%', // Top position relative to parent
				left : '50%' // Left position relative to parent
			};
			/*eslint no-undef: 0*/
			new Spinner(opts).spin(element[0]);
			// TODO This should probably get distroyed?
		};
	}


}

/**
 * Factory to create an instance of a BootSpinner.
 */
export default function BootSpinnerFactory(){
	return new BootSpinner();
}

'use strict';

/**
* Directive that will display the module name from the module code if available
*/
class CurrentModuleName {

	/**
	 * Creates a new instance of the CurrentModuleName directive
	 */
	constructor($rootScope, $route, registrationService){
		/**
		 * A reference to $rootScope.
		 */
		this.$rootScope = $rootScope;

		/**
		 * A reference to $route.
		 */
		this.$route = $route;

		/**
		 * A reference to RegistrationService.
		 */
		this.registrationService = registrationService;

		/**
		 * Directive restricted to Attribute.
		 */
		this.restrict = 'A';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = {
			'moduleName' : '='
		};

		const self = this;
		/**
		 * Linking function.
		 */
		this.link = function(){
			self.linkDirective(...arguments);
		};
	}

	/**
	 * Link function for the directive
	 */
	linkDirective(scope, element){
		this.$rootScope.$on('$locationChangeSuccess', () => {
			var newModuleId = this.$route.current.params ? this.$route.current.params.moduleId : null;

			// If there is no new value, we blank
			if(newModuleId == null || newModuleId == ''){
				element.text('');
			}
			// Else we need to lookup the new value
			else{
				this.registrationService.getRegistration().then((registrationData) => {
					var moduleName = '';
					if(registrationData && registrationData.modules && registrationData.modules[newModuleId]){
						moduleName = registrationData.modules[newModuleId].name;
					}
					element.text(moduleName);
				});
			}
		});
	}
}

/**
 * Factory to create a new instance of the directive
 */
var CurrentModuleNameFactory = function() {
	return new CurrentModuleName(...arguments);
};
CurrentModuleNameFactory.$inject = ['$rootScope', '$route', 'RegistrationService'];
export default CurrentModuleNameFactory;

'use strict';

/**
 * Directive to collapse a module
 */
class ModuleCollapse {

	/**
	 * Creates a new instance of the ModuleCollapse directive
	 */
	constructor($rootScope){
		/**
		 * A reference to $rootScope.
		 */
		this.$rootScope = $rootScope;

		/**
		 * Directive restricted to Attribute.
		 */
		this.restrict = 'A';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = false;
		const self = this;
		/**
		 * Linking function.
		 */
		this.link = function(){
			self.linkDirective(...arguments);
		};
	}

	/**
	 * Link function for the directive
	 */
	linkDirective(scope, element){

		var collapseButton = $(element.find('a')[0]), toCollapse = $(element.find('.collapse')[0]);
		const self = this;

		/**
		* Refresh the scrolle before and after modules has been collapsed
		*/
		function refreshScoller(){
			self.$rootScope.$broadcast('iScrollRefresh', {'scollerId' : 'mainScroll'});
		}

		toCollapse.on('shown.bs.collapse', refreshScoller);
		toCollapse.on('hidden.bs.collapse', refreshScoller);

		/**
		* Toggles the collapse of a module at the specified index
		*/
		collapseButton.on('click', function(){
			toCollapse.collapse('toggle');
		});
	}
}

/**
 * Factory to create a new instance of the directive
 */
var ModuleCollapseFactory = function() {
	return new ModuleCollapse(...arguments);
};
ModuleCollapseFactory.$inject = ['$rootScope'];
export default ModuleCollapseFactory;

'use strict';

/**
* Directive that will display the module name from the module code if available
*/
class ModuleName{

	/**
	 * Creates a new instance of the ModuleName directive
	 */
	constructor(moduleService){
		/**
		 * A reference to ModuleService.
		 */
		this.moduleService = moduleService;

		/**
		 * Directive restricted to Attribute.
		 */
		this.restrict = 'A';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = {
			'moduleName' : '='
		};

		const self = this;
		/**
		 * Linking function.
		 */
		this.link = function(){
			self.linkDirective(...arguments);
		};
	}

	/**
	 * Link function for the directive
	 */
	linkDirective(scope, element){
		const self = this;
		scope.$watch('moduleName', function(moduleId){
			if(moduleId == null){
				element.text('');
			}
			else{
				self.moduleService.getModuleName(moduleId).then(function(name){
					element.text(name ? name : 'unknown');
				});
			}
		});

	}
}

/**
 * Factory to create a new instance of the directive
 */
var ModuleNameFactory = function() {
	return new ModuleName(...arguments);
};
ModuleNameFactory.$inject = ['ModuleService'];
export default ModuleNameFactory;

'use strict';

class OverallSyncStatus{

	/**
	 * Creates a new instance of the OverallSyncStatus directive
	 */
	constructor(syncAPIService){
		/**
		 * A reference to SyncAPIService.
		 */
		this.syncAPIService = syncAPIService;

		/**
		 * Directive restricted to Class.
		 */
		this.restrict = 'C';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = {
			'tool' : '='
		};
		const self = this;
		/**
		 * Linking function.
		 */
		this.link = function(){
			self.linkDirective(...arguments);
		};
	}

	/**
	 * Link function for the directive
	 */
	linkDirective(scope, element){
		var cloudElement = $(element.find('.glyphicon')[0]);
		var self = this;
		function updateSyncIndicator(sync){

			/**
			* If there is no sync status, or if no sync action or state exists, we
			* have to perform a sync check our self to find out what the status is
			*/
			if(sync == null || (sync.state == null && sync.action == null)){
				self.syncAPIService.getSyncStatusModulesOffline().then(function(syncStatus){
					updateSyncIndicator({'action' : null, 'state' : (syncStatus.inSync ? 'inSync' : 'outSync')});
				});
			}
			/**
			* If there is a sync action, we use that first
			*/
			else if(sync.action != null && sync.action !== 'none'){
				cloudElement.addClass('syncBusy');
				cloudElement.removeClass('glyphicon-cloud');
				cloudElement.removeClass('inSync');
				cloudElement.addClass('outSync');
				if(sync.action === 'downloading'){
					cloudElement.addClass('glyphicon-cloud-download');
					cloudElement.removeClass('glyphicon-cloud-upload');

				}
				else if(sync.action === 'uploading'){
					cloudElement.removeClass('glyphicon-cloud-download');
					cloudElement.addClass('glyphicon-cloud-upload');
				}
			}
			// We are only working with the state
			else{
				cloudElement.removeClass('glyphicon-cloud-download');
				cloudElement.removeClass('glyphicon-cloud-upload');
				cloudElement.removeClass('syncBusy');
				cloudElement.addClass('glyphicon-cloud');
				if(sync.state === 'inSync'){
					cloudElement.addClass('inSync');
					cloudElement.removeClass('outSync');
				}
				else if(sync.state === 'outSync'){
					cloudElement.removeClass('inSync');
					cloudElement.addClass('outSync');
				}
			}
		}

		updateSyncIndicator(null);

		// Listen for sync state broadcasts
		scope.$on('syncStatusChanged', (event, state) => {
			updateSyncIndicator(state);
		});

	}
}

/**
 * Factory to create a new instance of the directive
 */
var OverallSyncStatusFactory = function(){
	return new OverallSyncStatus(...arguments);
};
OverallSyncStatusFactory.$inject = ['SyncAPIService'];
export default OverallSyncStatusFactory;

'use strict';
/**
* Directive to display a button with a list of attachments
*/
class SynthAttachments{

	/**
	 * Creates a new instance of the SynthAttachments directive
	 */
	constructor($filter){

		/**
		 * Directive restricted to Element.
		 */
		this.restrict = 'E';

		/**
		 * URL of template to use.
		 */
		this.templateUrl = 'base/partials/directives/attachments.html';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = {
			'attachments' : '='
		};

		/**
		 * A reference to $filter.
		 */
		this.$filter = $filter;

		const self = this;
		/**
		 * Linking function.
		 */
		this.link = function(){
			self.linkDirective(...arguments);
		};
	}


	/**
	 * Link function for the directive
	 */
	linkDirective(scope, element, attr){
		// Open an attachment natively on the device
		scope.openAttachment = function(attachment){

			window.plugins.FileOpener.open(attachment.downloadPath,
				function(){

				},
				function(){
					//alert(error);
				});
		};

		// The attachments we need to handle
		scope.attachments = this.$filter('attachments')(scope.attachments);

		// If the button needs to be pulled in a direction
		if (attr.pull){
			if (attr.pull === 'right'){
				scope.buttonPull = 'pull-right';
			}
			else if (attr.pull === 'left'){
				scope.buttonPull = 'pull-left';
			}
			else{
				scope.buttonPull = null;
			}
		}
		else{
			scope.buttonPull = null;
		}
	}
}

/**
 * Factory to create new instances of the SynthAttachments directive.
 */
var SynthAttachmentsFactory = function(){
	return new SynthAttachments(...arguments);
};
SynthAttachmentsFactory.$inject = ['$filter'];
export default SynthAttachmentsFactory;

'use strict';
/**
* Directive to create the footer for the application
*/
class SynthFooter{

	/**
	 * Creates a new instance of the SynthFooter directive
	 */
	constructor(){
		/**
		 * Directive restricted to Element.
		 */
		this.restrict = 'E';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = false;

		/**
		 * URL of template to use.
		 */
		this.templateUrl = 'base/partials/directives/footer.html';
	}
}

/**
 * Factory to create new instances of the SynthFooter directive.
 */
var SynthFooterFactory = () => {
	return new SynthFooter();
};

export default SynthFooterFactory;

'use strict';

class SynthMenu {

	/**
	 * Creates a new instance of the SynthMenu directive
	 */
	constructor($location, userService){
		/**
		 * Directive restricted to Element.
		 */
		this.restrict = 'E';

		/**
		 * Isolated scope for directive.
		 */
		this.scope = false;

		/**
		 * URL of template to use.
		 */
		this.templateUrl = 'base/partials/directives/menu.html';

		/**
		 * A reference to $location.
		 */
		this.$location = $location;

		/**
		 * A reference to UserService.
		 */
		this.userService = userService;
		const self = this;

		/**
		 * Linking function.
		 */
		this.link = function(){
			self.linkDirective(...arguments);
		};
	}

	/**
	 * Link function for the directive
	 */
	linkDirective(scope){
		const $location = this.$location,
			self = this;
		scope.menuOpen = false;
		scope.isRegistered = false;

		scope.toggleMenu = () => {
			scope.menuOpen = !scope.menuOpen;
		};
		scope.openLink = (link) => {
			scope.menuOpen = false;
			$location.path(link);
		};

		function updateCompleteFlag(){
			self.userService.isRegistrationComplete().then((iscomplete) => {
				scope.isRegistered = iscomplete;
			});
		}
		updateCompleteFlag();

		scope.$on('registrationDataChanged', () => {
			updateCompleteFlag();
		});


		// Open sync page if user is registered
		scope.openSync = () => {
			// Make sure the close the menu that could maybe be open
			scope.menuOpen = false;

			if(scope.isRegistered){
				$location.path('/sync');
			}
		};

	}
}

/**
 * Factory to create new instances of the SynthMenu directive
 */
var SynthMenuFactory = function() {
	return new SynthMenu(...arguments);
};
SynthMenuFactory.$inject = ['$location', 'UserService'];
