'use strict';
/**
* Filter to convert bytes to a size
*/
var BytesToSizeFilter = function(bytes, showZero) {
	var sizes = ['B', 'KB', 'MB', 'GB', 'TB'];

	if (showZero && bytes === 0){
		return '0';
	}

	if (bytes === undefined || bytes === 0 ) {
		return 'n/a';
	}
	var i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)));
	if (i === 0) {
		return bytes + ' ' + sizes[i];
	}
	return (bytes / Math.pow(1024, i)).toFixed(1) + ' ' + sizes[i];
};

/**
 * Factory to create new instances of the BytesToSizeFilter
 */
export default function BytesToSizeFilterFactory(){
	return BytesToSizeFilter;
}

'use strict';
/**
 * Filter to format a date
 */
var FormatDateFilter = function(date, format) {

	// If there is no format use a default format
	if (!format) {
		format = 'YYYY-MM-DD h:mm a';
	}

	// If there is no date, return an empty string
	if (date === null || date === '') {
		return '';
	}
	return moment(date).format(format);
};

/**
 * Factory to create new instances of the FormatDateFilter
 */
export default function FormatDateFilterFactory(){
	return FormatDateFilter;
}

