/*
 * base/js/register/RegisterCtrl.js
 */
var RegisterCtrl = ($scope, $rootScope, $location, $q, SynthesisRESTClient, DataService, SynthErrorHandler, SynthError, PushService) => {
	$rootScope.activePage = 'register';
	$rootScope.breadcrumbs = [{'name' : 'Register'}];
	$scope.authFailed = false;
	$scope.login = {username : '', password : ''};
	$scope.busyLogin = false;
	$scope.submitButtonTxt = 'Sign in';

	var funcAuthenticateUser = function(){
			funcSigningIn(true);
			var username = $scope.login.username;
			var password = $scope.login.password;
			return SynthesisRESTClient
				.authenticateUser(username, password)
				.then((result) => {
					funcSigningIn(false);
					if(!result.authenticated){
						// $scope.login.password = null; // Clear entered password
						return $q.reject(SynthError(result));
					}
					return undefined;
				});
		},
		funcCompleted = function(){
			// We don't need to wait for this to complete, it must just happen
			PushService.registerDevice();
			$location.path('/register-selectModules');
		},
		funcFailed = function(reason){
			funcSigningIn(false);
			return SynthErrorHandler(reason);
		},
		funcSigningIn = function(isBusy){
			$scope.submitButtonTxt = isBusy ? 'Signing in' : 'Sign in';
			$scope.busyLogin = isBusy;
		};

	// Submit handler
	$scope.submit = function(){
		funcAuthenticateUser().then(funcCompleted, funcFailed);
	};
};
RegisterCtrl.$inject = ['$scope', '$rootScope', '$location', '$q', 'SynthesisRESTClient', 'DataService', 'SynthErrorHandler', 'SynthError', 'PushService'];
export default RegisterCtrl;

'use strict';

/**
* Controller to register the modules with the sync engine
*/
var RegisterModuleRegistrationCtrl = ($scope, $rootScope, $location, UserSession, DataService, RegisterService, LoggerService, SynthErrorHandler, SynthAuthenticateUser, ModuleService) =>{
	var LOG = LoggerService('RegisterModuleRegistrationCtrl');

	$rootScope.activePage = 'register-moduleRegistration';
	$rootScope.breadcrumbs = [{'name' : 'Registering Modules'}];
	$scope.error = false;
	$scope.busy = true;

	/*
	* Check that the user still has a registration object on the user session.
	* This object might be deleted if the user presses back after completing a
	* registration
	*/
	if (UserSession.registration.modules == null){
		LOG.info('There are no modules for active user');
		$location.path('/register');
		return;
	}

	ModuleService.getLinkedModules().then((registeredModulesObj) => {
		var modules = [];

		angular.forEach(UserSession.registration.modules, (module) => {
			if(!(registeredModulesObj[module.id] != null)){
				modules.push(module);
			}
		});

		// If there are no modules to register for, we are done
		if(modules.length == 0){
			$location.path('/home');
			return;
		}

		// Add the modules to the scope and start the registration
		$scope.modules = modules;
		startRegistrations();
	});

	var funcInitModules = function(){
			return RegisterService.initModules($scope.modules);
		},
		funcRegisterModules = function(){
			return RegisterService.registerModules($scope.modules);
		},
		funcGoToSync = function(){
			$scope.busy = false;
			$location.path('/sync');
		},
		funcHandleError = function(reason){

			if(reason.id === 2002){
				SynthAuthenticateUser
				.login('Please enter password', 'Sync')
				.then((result) => {
					if(SynthAuthenticateUser.FAILED == result.code){
						LOG.warn('Authentication failed');
					}
					else if(SynthAuthenticateUser.SUCCESS == result.code){
						startRegistrations();
					}
					else{
						$scope.errorMessage = 'You need to authenticate';
						$scope.error = true;
					}
				});
			}
			else{
				SynthErrorHandler(reason).then(() => {}, () =>{
					LOG.error('Error while trying to register for modules');
					$scope.error = true;
				});
			}
		};

	function startRegistrations(){
		funcInitModules()
		.then(funcRegisterModules)
		.then(funcGoToSync, funcHandleError);
	}
};
RegisterModuleRegistrationCtrl.$inject = ['$scope', '$rootScope', '$location', 'UserSession', 'DataService', 'RegisterService', 'LoggerService', 'SynthErrorHandler', 'SynthAuthenticateUser', 'ModuleService'];
export default RegisterModuleRegistrationCtrl;

'use strict';

/**
* Controller to allow the user to select modules that should be used by this application
*/
var RegisterSelectModuleCtrl = ($q, $scope, $rootScope, $location, ModuleService, DataService, UserSession, LoggerService, SyncAPIService, SynthErrorHandler, SynthAuthenticateUser, RegistrationService) => {
	var LOG = LoggerService('RegisterSelectModuleCtrl');

	$rootScope.activePage = 'register-selectModule';
	$rootScope.breadcrumbs = [{'name' : 'Select Modules'}];

	// Add properties to scope
	angular.extend($scope, {
		error : false,
		errorMessage : null,
		canContinue : false,
		loadingModules : true,
		modules : null,
		// Bind to function
		hasSelectedModules : false,
		hasNewModules : false
	});
	var firstTime = true;


	var getSelectedModules = function(){
			var modules = $scope.modules;

			// If there is no modules, we have nothing
			if(modules == null){
				return null;
			}
			var selectedModules = [];

			_.forEach(modules, (module) => {
				if (module.selected && !module.registered){
					var newModule = angular.copy(module);
					delete newModule.selected;
					selectedModules.push(newModule);
				}
			});
			return selectedModules;
		},
		// Get the first module that the user selected
		getFirstSelectedModule = function(){
			var selectedModules = getSelectedModules();
			return selectedModules == null ? null : selectedModules[0];
		},
		checkSelectedModules = function(){
			$scope.hasSelectedModules = getFirstSelectedModule() != null;

			if(firstTime || $scope.hasSelectedModules){
				$scope.error = false;
				$scope.canContinue = true;
				firstTime = false;
			}
			else{
				warnSelectModule();
			}
		},

		// Warns the user that a module must be selected
		warnSelectModule = function(){
			angular.extend($scope, {
				error : true,
				errorMessage : 'You must select atleast one module',
				canContinue : false
			});
		};

	function getModules(){
		$scope.loadingModules = true;
		var currentModules, newModules;

		// Save the newly discover modules to our list of known modules
		function saveNewModules(){

			// If there is now new modules we don't have to save anything
			if(newModules.lenth === 0){
				return $q.when([]);
			}

			var mergeMap = { modules : {}};

			_.forEach(newModules, (newModule) => {
				mergeMap.modules[newModule.id] = newModule;
			});

			return RegistrationService.mergeRegistration(mergeMap);
		}

		function getCurrentModules(){
			return ModuleService.getCurrentModules().then((modules) => {
				currentModules = modules;
			});
		}

		function getNewModules(){
			return ModuleService.getNewModules().then((modules) => {
				$scope.hasNewModules = modules.length > 0;
				newModules = modules;
			});
		}

		function concatModules(){
			$scope.loadingModules = false;
			return $q.when(currentModules.concat(newModules));
		}

		getCurrentModules()
			.then(getNewModules)
			.then(saveNewModules)
			.then(concatModules)
			.then((modules) => {
				$scope.loadingModules = false;
				$scope.modules = modules;
				if($scope.modules.length == 0){
					angular.extend($scope, {
						error : true,
						errorMessage : 'You are not registered for any modules',
						canContinue : false,
						modules : null
					});
				}
				checkSelectedModules();
			}, (reason) => {
				$scope.loadingModules = false;

				if(reason.id === 2002){
					SynthAuthenticateUser
					.login('Please enter password', 'Sync')
					.then(function(result){
						if(SynthAuthenticateUser.FAILED == result.code){
							LOG.warn('Authentication failed');
						}
						else if(SynthAuthenticateUser.SUCCESS == result.code){
							getModules();
						}
						else{
							$scope.errorMessage = 'You need to authenticate';
							$scope.error = true;
						}
					});
				}
				else{
					SynthErrorHandler(reason).then(() => {}, () => {
						$scope.errorMessage = 'Failed to retrieve modules';
						$scope.error = true;
					});
				}
			});
	}


	// Toggle the selection of a module
	$scope.toggleModule = function(module){

		// Do nothing if the user is already registered for the module
		if(module.registered){
			return;
		}

		module.selected = !module.selected;
		checkSelectedModules();
	};


	// Function to handle submit of the form
	$scope.submit = function(){

		// Check that there was a selection
		if (!$scope.hasSelectedModules){
			$scope.errorMessage = 'You must select atleast one module';
			$scope.error = true;

		/*
		* If there was modules selected, we will go to the next page
		* where the device will register with the service that those
		* modules will be used on the device.
		*/
		}
		else{
			// Get the modules the user choose
			UserSession.registration = UserSession.registration || {};
			UserSession.registration.modules = getSelectedModules();

			// Go to the next page where the modules will get registered
			$location.path('/register-modulesRegistration');
		}
	};

	getModules();

};
RegisterSelectModuleCtrl.$inject = ['$q', '$scope', '$rootScope', '$location', 'ModuleService', 'DataService', 'UserSession', 'LoggerService', 'SyncAPIService', 'SynthErrorHandler', 'SynthAuthenticateUser', 'RegistrationService'];
