		 *		    'announcements': false,
		 *		    'faq': false,
		 *		    'forums': false,
		 *		    'welcome': false,
		 *		    'schedule': false,
		 *		    'learning_units': false,
		 *		    'resources': false
		 *		  },
		 *		'moduleId1': {
		 *		    'announcements': false,
		 *		    'faq': false,
		 *		    'forums': false,
		 *		    'welcome': false,
		 *		    'schedule': false,
		 *		    'learning_units': false,
