'use strict';
var AnnouncementsDeleteHandler = ($q, $timeout) => {

	return function(data){
		if(data == null || Object.keys(data).length === 0){
			return $q.when({
				data : data,
				deleted : []
			});
		}

		var deferred = $q.defer();
		$timeout(function(){
			var returnData = {
				data : {},
				deleted : []
			};
			// Step through each item
			for (var announcementKey in data) {
				var announcement = data[announcementKey];

				// If one is found with 'DELETE'
				if(announcement.status || announcement.status == 'DELETED'){
					// Add files of the announcement to delete
					// returnData.deleted.push();
				}
				else{
					// Keep the item in the data list
					returnData.data[announcementKey] = announcement;
				}
			}
			// return the fixed data
			deferred.resolve(returnData);
		});
		return deferred.promise;
	};

};
AnnouncementsDeleteHandler.$inject = ['$q', '$timeout'];
export default AnnouncementsDeleteHandler;

'use strict';

var AnnouncementsImageHandler = ($q, SynthEmbeddedImageHandler) => {

	return (toolContent) => {
		var announcementKeys = Object.keys(toolContent);

		/**
		 * If there is no annoucment content, there is nothing to
		 * attempt to change
		 */
		if(announcementKeys.length === 0){
			return $q.when(toolContent);
		}

		let promise = $q.when();
		angular.forEach(announcementKeys, function(announcementKey){
			promise = promise.then(function(){
				let announcement = toolContent[announcementKey];
				return SynthEmbeddedImageHandler.fixForHtmlElement(announcement.body, function(imagePath){
					return SynthEmbeddedImageHandler.resolveImageLocalPathFromAttachments(imagePath, announcement.attachments || []);
				})
				.then((fixedContent) =>{
					// Update the content to the newly fixed html content
					toolContent[announcementKey].body = fixedContent;
				});
			});
		});
		return promise.then(()=>{
			return toolContent;
		});
	};

};
AnnouncementsImageHandler.$inject = ['$q', 'SynthEmbeddedImageHandler'];

export default AnnouncementsImageHandler;

'use strict';
var AnnouncementsLinkHandler = ($q, SynthLinkHandler) => {

	return (toolContent) => {

		// If there is not data to look at
		if(toolContent == null || Object.keys(toolContent).length === 0){
			return $q.when(toolContent);
		}
		var announcementKeys = Object.keys(toolContent);
		let promise = $q.when();
		angular.forEach(announcementKeys, function(annoucementKey){
			promise = promise.then(function(){
				let announcement = toolContent[annoucementKey];
				return SynthLinkHandler.fixContent(announcement.body).then((fixedContent)=>{
					announcement.body = fixedContent;
				});
			});
		});

		return promise.then(()=>{
			return toolContent;
		});
	};
};
